CREATE OR REPLACE FUNCTION public.log_activity(p_user_id uuid, p_action_type text, p_giftlist_id uuid DEFAULT NULL::uuid, p_gift_id uuid DEFAULT NULL::uuid)
 RETURNS void
 LANGUAGE plpgsql
AS $function$
DECLARE
    v_action_type_id INT;
BEGIN
    -- Intentar obtener el ID del tipo de acción
    SELECT id INTO v_action_type_id FROM action_types WHERE name = p_action_type;

    -- Si no existe, insertar un nuevo tipo de acción
    IF v_action_type_id IS NULL THEN
        INSERT INTO action_types (name) VALUES (p_action_type) RETURNING id INTO v_action_type_id;
    END IF;

    -- Insertar el registro de actividad
    INSERT INTO activity_log (user_id, giftlist_id, gift_id, action_type_id)
    VALUES (p_user_id, p_giftlist_id, p_gift_id, v_action_type_id);
END;
$function$

CREATE OR REPLACE FUNCTION public.delete_gift(p_gift_id uuid, p_user_id uuid)
 RETURNS void
 LANGUAGE plpgsql
AS $function$
DECLARE
    v_giftlist_id UUID;
BEGIN
    -- Verificar si el regalo existe y no está ya eliminado
    IF NOT EXISTS (SELECT 1 FROM gifts WHERE id = p_gift_id AND is_deleted = FALSE) THEN
        RAISE EXCEPTION 'Gift not found or already deleted';
    END IF;

    -- Obtener el giftlist_id antes de marcar el regalo como eliminado
    SELECT giftlist_id INTO v_giftlist_id FROM gifts WHERE id = p_gift_id;

    -- Marcar el regalo como eliminado y establecer delete_date
    UPDATE gifts
    SET is_deleted = TRUE,
        delete_date = CURRENT_TIMESTAMP
    WHERE id = p_gift_id;

    -- Registrar la actividad
    PERFORM log_activity(p_user_id, 'delete_gift', v_giftlist_id, p_gift_id);
END;
$function$

CREATE OR REPLACE FUNCTION public.analyze_gift_tables()
 RETURNS TABLE(table_name text, analyzed boolean, message text)
 LANGUAGE plpgsql
AS $function$
DECLARE
    tables text[] := ARRAY['gifts', 'giftlists', 'giftusers', 'giftlistusers', 'users', 'roles', 'giftstates'];
    t text;
BEGIN
    FOREACH t IN ARRAY tables
    LOOP
        BEGIN
            EXECUTE 'ANALYZE ' || t;
            table_name := t;
            analyzed := true;
            message := 'Successfully analyzed';
            RETURN NEXT;
        EXCEPTION
            WHEN OTHERS THEN
                table_name := t;
                analyzed := false;
                message := 'Error: ' || SQLERRM;
                RETURN NEXT;
        END;
    END LOOP;
END;
$function$

CREATE OR REPLACE FUNCTION public.associate_user_to_gift_list(p_gift_list_id uuid, p_user_id uuid, p_role text)
 RETURNS TABLE(giftlist_id uuid, user_id uuid, role_name text)
 LANGUAGE plpgsql
AS $function$
DECLARE
  v_role_id INT;
BEGIN
  -- Existing logic...
  SELECT id INTO v_role_id FROM roles WHERE name = p_role;
  IF v_role_id IS NULL THEN
    RAISE EXCEPTION 'Role not found: %', p_role;
  END IF;
  IF EXISTS (
    SELECT 1 FROM giftlistusers glu
    WHERE glu.giftlist_id = p_gift_list_id AND glu.user_id = p_user_id AND glu.role_id = 1
  ) THEN
    RAISE EXCEPTION 'User is already the owner of the gift list';
  END IF;
  INSERT INTO giftlistusers (giftlist_id, user_id, role_id)
  VALUES (p_gift_list_id, p_user_id, v_role_id);

  -- Log activity
  PERFORM log_activity(p_user_id, 'associate_user_to_giftlist', p_gift_list_id, NULL);

  -- Return result
  RETURN QUERY
  SELECT glu.giftlist_id, glu.user_id, r.name::TEXT AS role_name
  FROM giftlistusers glu
  JOIN roles r ON glu.role_id = r.id
  WHERE glu.giftlist_id = p_gift_list_id AND glu.user_id = p_user_id;
END;
$function$
